import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './styles.css';
import { recipeData } from './recipeData'; // Import the recipeData constant
import './index.css';
import RecipeDetails from './RecipeDetails';
import { RecipeCard } from './RecipeCard';

function App() {
  const [query, setQuery] = useState('');
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [searched, setSearched] = useState(false);

  function handleSearch(query) {
    setQuery(query);
    setSearched(true);
  }

  function handleGoBack() {
    setSearched(false);
    setQuery('');
  }

  useEffect(() => {
    const filtered = recipeData.filter(recipe =>
      recipe.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredRecipes(filtered.length > 0 ? filtered : []);
  }, [query]);

  return (
    <div>
      <h1 className='header'>My Recipe App</h1>
      {!searched ? (
        <SearchBar onSearch={handleSearch} />
      ) : (
        <button onClick={handleGoBack}>Go Back</button> //allows you to go back
      )}
      <div className="recipe-container">
        {filteredRecipes.map(recipe => (
          <RecipeCard
          key={recipe.title}
          title={recipe.title}
          imageUrl={recipe.imageUrl}
          cuisine={recipe.cuisine}
          ingredients={recipe.ingredients}
          instructions={recipe.instructions}
          />
        ))}
      </div>
    </div>
  );
}

function SearchBar({ onSearch }) {
  const [query, setQuery] = useState('');

  function handleSearch() {
    onSearch(query);
  }

  function handleQueryChange(event) {
    setQuery(event.target.value);
  }

  return (
    <div className='search-bar'>
      <input
        type="text"
        placeholder="Search recipes..."
        value={query}
        onChange={handleQueryChange}
      />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

function VoteButton() {
  return (
    <button>Vote</button>
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
