import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const recipes = [
    {
      title: 'Recipe 1',
      description: 'Description of Recipe 1',
      ingredients: ['Ingredient 1', 'Ingredient 2', 'Ingredient 3'],
      instructions: 'Instructions for Recipe 1',
    },
    {
      title: 'Recipe 2',
      description: 'Description of Recipe 2',
      ingredients: ['Ingredient 4', 'Ingredient 5', 'Ingredient 6'],
      instructions: 'Instructions for Recipe 2',
    },
    // Add more recipes as needed
  ];

  return (
    <div className="recipe-container">
      {recipes.map((recipe, index) => (
        <div className="recipe-card" key={index}>
          <h2>{recipe.title}</h2>
          <p>{recipe.description}</p>
          <Link to={`/recipes/${recipe.title}`}>Click for more details</Link>
        </div>
      ))}
    </div>
  );
};

export default HomePage;
